import React, { useState, useEffect } from 'react';
// import Header from './Search';

import axios from 'axios';
const id = Math.ceil(Math.random()*1000);
export default function App(props) {
  const [messages, setMessages] = useState([]);
  const [usernames, setUsername] = useState([]);

  const [input, setInput] = useState('');
  const [inputname, setInputname] = useState('');



  useEffect(() => {
    // setInterval(

      // () => 
      // axios.get('http://localhost:3000/messages').then(res => {
      //   //data is the body of the response 
      //   setMessages(res.data);
      //   setUsername(res.data);

      // })
      axios.post('http://localhost:3000/subscribe', 
      { id }).then(res => {

        setMessages(messages.concat(res.data));
        setUsername(usernames.concat(res.data));



      }, []) //only one when the component is opend   //end of effect 


      // , 100 * 1000)


  }, []) //only one when the component is opend   //end of effect 

  const myChangeHandler = (e) => {
    const{target:{value}}=e;
setInput(value);

      }
      const nameChangeHandler = (e) => {
        const{target:{value}}=e;
    setInputname(value);

          }

      const handleSubmit = (e) => {
        e.preventDefault(); //prevent refresh

        // axios.post('http://localhost:3000/messages',{content:input,username:inputname})//in your body post the content from value

        axios.post('http://localhost:3000/messageSubscribers',{content:input,username:inputname})//in your body post the content from value

          }


  return (
    <div>
      {
        messages.map(message => <h1 key={message.content}>{message.content}</h1>)
      }
      {usernames.map((username)=><h1 key = {username.username}>{username.content}</h1>)
      }
      {
        <form id = "form" onSubmit={handleSubmit}>
          <h1>Hello</h1>
          <p>Enter your name:{usernames.content} </p>
          <input
            type='text'
            name="content"
            id="content"
            placeholder="message"
            onChange={myChangeHandler}
            value={input}
          />
           <input
            type='text'
            name="contentname"
            id="content"
            placeholder="username"
            onChange={nameChangeHandler}
            value={inputname}
          />
          <button type="submit" >send</button>
        </form>

      }

    </div>


  )
}

