import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Sse () {

    const [messages, setMessages] = useState([]);
    const [usernames, setUsername] = useState([]);

    const [input, setInput] = useState('');
    const [inputname, setInputname] = useState('');

    useEffect(() => {

           const eventSource =new EventSource ('http://localhost:5005/messages');
           eventSource.onmessage=
             (e)=>{
             console.log(e.data);
             const message = JSON.parse(e.data);
            setMessages(messages => messages.concat(message));

          };




    }, []);
    const myChangeHandler = (e) => {
        const { target: { value } } = e;
        setInput(value);

      }
      const nameChangeHandler = (e) => {
        const { target: { value } } = e;
        setInputname(value);

      }

      const handleSubmit = (e) => {
        e.preventDefault(); //prevent refresh

        // axios.post('http://localhost:5005/messages',{content:input,username:inputname})//in your body post the content from value

        axios.post('http://localhost:5005/messageSubscribers', { content: input, username: inputname })//in your body post the content from value

      }


    return (
        <div>
          {
            messages.map(message => <h1 key={message.content}> {message.username} :: {message.content} </h1>)
          }
          {/* {usernames.map((username) => <h1 key={username.username}> user : {username.username} </h1>) */}
          }
          {
            <form id="form" onSubmit={handleSubmit}>
              <h1>our chat</h1>
              {/* <p>Enter your name:{usernames.content} </p> */}
              <input
                type='text'
                name="content"
                id="content"
                placeholder="message"
                onChange={myChangeHandler}
                value={input}
              />
              <input
                type='text'
                name="contentname"
                id="content"
                placeholder="username"
                onChange={nameChangeHandler}
                value={inputname}
              />
              <button type="submit" >send</button>
            </form>

          }

        </div>


      )

        }
export default Sse;
